import React, { useEffect, useState } from 'react';
import twitterLogo from './assets/twitter-logo.svg';
import './App.css';


// Constants
const TWITTER_HANDLE = 'Buildspace';
const TWITTER_LINK = `https://twitter.com/${TWITTER_HANDLE}`;

const App = () => {
  // State
  const [walletAddress, setWalletAddress] = useState(null);

  // Actions
  const checkIfWalletIsConnected = async () => {
    try {
      const { solana } = window;

      if (solana) {
        if (solana.isPhantom) {
          console.log('Phantom wallet found!');
          const response = await solana.connect({ onlyIfTrusted: true });
          console.log('Connected with Public Key:', response.publicKey.toString()
          );


          /*
           * Set the user's publicKey in state to be used later!
           */
          setWalletAddress(response.publicKey.toString());
        }
      } else {
        alert('Solana object not found! Get a Phantom Wallet ðŸ‘»');
      }
    } catch (error) {
      console.error(error);
    }
  };

  const connectWallet = async () => {
    const { solana } = window;

    if (solana) {
      const response = await solana.connect();
      console.log('Connected with Public Key:', response.publicKey.toString());
      setWalletAddress(response.publicKey.toString());
    }
  };




  const renderNotConnectedContainer = () => (
    <button
      className="cta-button connect-wallet-button button"
      onClick={connectWallet}
    >
      Connect to Wallet
    </button>
  );


  const renderConnectedContainer = () => (
 <><><div className="walletAddress">
      <p>Connected Wallet: {walletAddress}</p>
    </div><div className="formSection">
        <h2>Enter your information below:</h2>
      </div></><button
        className="cta-button connect-wallet-button button"
        onClick={disconnectWallet}
      >
        Connect to Wallet
      </button></>
  );

  function disconnectWallet()
{
  window.solana.disconnect();
  window.solana.on('disconnect', () => console.log("disconnected!"))
}



  // UseEffects
  useEffect(() => {
    const onLoad = async () => {
      await checkIfWalletIsConnected();
    };
    window.addEventListener('load', onLoad);
    return () => window.removeEventListener('load', onLoad);
  }, []);

  return (



    <div className="App">
      <div className="container">
        <div className="header-container">
          <p className="header">ðŸª‚ Solana Airdrop Tool</p>
          <p className="sub-text">
            Aidrop Solana or SPL tokens to one or multiple solana addresses.
          </p>
          {!walletAddress && renderNotConnectedContainer()}
          {/* We just need to add the inverse here! */}
          {walletAddress && renderConnectedContainer()}
        </div>



        <div className="footer-container">
          <img alt="Twitter Logo" className="twitter-logo" src={twitterLogo} />
          <a
            className="footer-text"
            href={TWITTER_LINK}
            target="_blank"
            rel="noreferrer"
          >{`built on @${TWITTER_HANDLE}`}</a>
        </div>
      </div>
    </div>
  );
};

export default App;
